(1629, 'en', 'default', '4c88924788f419b562d50acfddc3a781', 'Sunrise', NULL, NULL, 'LINE', NULL, 2, 'admin color scheme', '19f9d72989401694ea1e7e15ad0a683d', '', NULL),
(1630, 'en', 'default', 'c0c760de7ec190b80ab7e9f641c1d0fe', 'Ectoplasm', NULL, NULL, 'LINE', NULL, 2, 'admin color scheme', '58b08c800ea7646643655376b2db38a4', '', NULL),
(1631, 'en', 'default', 'f41054884f4a8c8cb596222e2a31f098', 'Ocean', NULL, NULL, 'LINE', NULL, 2, 'admin color scheme', '48a4b6317f44f657ae9e91596d66631c', '', NULL),
(1632, 'en', 'default', '3c09b8d09553786b799e02306e55c7ba', 'Coffee', NULL, NULL, 'LINE', NULL, 2, 'admin color scheme', 'e1210d7b53d29dba608ee1eddf04fab9', '', NULL),
(1633, 'en', 'default', '2b93564fb7b857efd28a284746e20944', 'Suggested text:', NULL, NULL, 'LINE', NULL, 2, '', 'dd2a2944817b466b15457763acc73e68', '', NULL),
(1634, 'en', 'default', '6d54357de16dae873aca455b2a0757b4', 'Who we are', NULL, NULL, 'LINE', NULL, 2, '', '4674e96347c0620f35ae54a960e8acf3', '', NULL),
(1635, 'en', 'default', '36ce374000d338cd986460ce312bcae5', 'In this section you should note your site URL, as well as the name of the company, organization, or individual behind it, and some accurate contact information.', NULL, NULL, 'LINE', NULL, 2, '', '3ee9c2dd60584a7dde9b3eda4294c2d0', '', NULL),
(1636, 'en', 'default', '16a7d315d4eb5e71cf53bd119764f2a6', 'The amount of information you may be required to show will vary depending on your local or national business regulations. You may, for example, be required to display a physical address, a registered address, or your company registration number.', NULL, NULL, 'LINE', NULL, 2, '', '1309f621bd0055cd2c9d869996990f0c', '', NULL),
(1637, 'en', 'default', '60c69c85078faab94c4ad122825e041b', 'Our website address is: %s.', NULL, NULL, 'LINE', NULL, 2, '', '9864bae3e2831914ba049efb8e2e3b08', '', NULL),
(1638, 'en', 'default', 'a9db3b2eaf00fc9d72b684237b8c004f', 'What personal data we collect and why we collect it', NULL, NULL, 'LINE', NULL, 2, '', '124ca84a9d92a67152c96ca3e975ac7d', '', NULL),
(1639, 'en', 'default', 'afb5c651f25a99fe9dea21458989b965', 'In this section you should note what personal data you collect from users and site visitors. This may include personal data, such as name, email address, personal account preferences; transactional data, such as purchase information; and technical data, such as information about cookies.', NULL, NULL, 'LINE', NULL, 2, '', '55364a8c7c34dcaec179a49ce5feb8ac', '', NULL),
(1640, 'en', 'default', '2b0bb6f25d9c926d8717eae9d9a94454', 'You should also note any collection and retention of sensitive personal data, such as data concerning health.', NULL, NULL, 'LINE', NULL, 2, '', 'cf565abf2a4b72a4ba7401b2f229d7b4', '', NULL),
(1641, 'en', 'default', 'ac9a4d2e8722460793b93d7d7ceeb7a5', 'In addition to listing what personal data you collect, you need to note why you collect it. These explanations must note either the legal basis for your data collection and retention or the active consent the user has given.', NULL, NULL, 'LINE', NULL, 2, '', 'adcb013b4f3ca5b3df135d1b9f0798fb', '', NULL),
(1642, 'en', 'default', '61ea2b8d21fe2e4df2247f0680579024', 'Personal data is not just created by a user&#8217;s interactions with your site. Personal data is also generated from technical processes such as contact forms, comments, cookies, analytics, and third party embeds.', NULL, NULL, 'LINE', NULL, 2, '', '9a4977aaf3242145fdb1214580d78be8', '', NULL),
(1643, 'en', 'default', 'a4de3012edb9e615cba657b2fb29dd24', 'By default WordPress does not collect any personal data about visitors, and only collects the data shown on the User Profile screen from registered users. However some of your plugins may collect personal data. You should add the relevant information below.', NULL, NULL, 'LINE', NULL, 2, '', 'eec5f990e54f0d488c9668381d51ec1c', '', NULL),
(1644, 'en', 'default', 'b619d7c9609b9d7b1ca3e6bfbcf5309e', 'In this subsection you should note what information is captured through comments. We have noted the data which WordPress collects by default.', NULL, NULL, 'LINE', NULL, 2, '', 'cdf36a9314632abfed28786bb7267047', '', NULL),
(1645, 'en', 'default', 'b18d07e59a875a4350e8cfa10509c5d7', 'When visitors leave comments on the site we collect the data shown in the comments form, and also the visitor&#8217;s IP address and browser user agent string to help spam detection.', NULL, NULL, 'LINE', NULL, 2, '', 'da2d58e7f047ffb8843c16d6ddf10432', '', NULL),
(1646, 'en', 'default', '5bf0318ac2be1384d56f30c969c0220d', 'An anonymized string created from your email address (also called a hash) may be provided to the Gravatar service to see if you are using it. The Gravatar service privacy policy is available here: https://automattic.com/privacy/. After approval of your comment, your profile picture is visible to the public in the context of your comment.', NULL, NULL, 'LINE', NULL, 2, '', 'fbae205b13903785c133763abea985cd', '', NULL),
(1647, 'en', 'default', 'ec27ccde34cd289565d2db48b580f59b', 'In this subsection you should note what information may be disclosed by users who can upload media files. All uploaded files are usually publicly accessible.', NULL, NULL, 'LINE', NULL, 2, '', '2c605f73abd62a98d1527efab250fad6', '', NULL),
(1648, 'en', 'default', '64771a7ce98863e88b3b4e2c1448f6d0', 'If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. Visitors to the website can download and extract any location data from images on the website.', NULL, NULL, 'LINE', NULL, 2, '', '69ef71fb437c6bf62fc83a4ccf0f54b5', '', NULL),
(1649, 'en', 'default', '0c7b687f084390a829fa8565b3096029', 'Contact forms', NULL, NULL, 'LINE', NULL, 2, '', '3a8f1f1123b64bb049198019beed2706', '', NULL),
(1650, 'en', 'default', 'bdcc5a96190089ab92a53a42910fc9d7', 'By default, WordPress does not include a contact form. If you use a contact form plugin, use this subsection to note what personal data is captured when someone submits a contact form, and how long you keep it. For example, you may note that you keep contact form submissions for a certain period for customer service purposes, but you do not use the information submitted through them for marketing purposes.', NULL, NULL, 'LINE', NULL, 2, '', '2f597e938edf13361bdf1cde1770b525', '', NULL),
(1651, 'en', 'default', '597b56e53847cd6a4712ac183f61fa68', 'Cookies', NULL, NULL, 'LINE', NULL, 2, '', 'dfb4c3735daecf9a7310dc58a2e575f8', '', NULL),
(1652, 'en', 'default', 'ca3d5159accd9468729ea6a9aef4d58e', 'In this subsection you should list the cookies your web site uses, including those set by your plugins, social media, and analytics. We have provided the cookies which WordPress installs by default.', NULL, NULL, 'LINE', NULL, 2, '', 'a7aa56fcc29c49d74ac60bfadbb28ae7', '', NULL),
(1653, 'en', 'default', 'b642d452dd24b19ed95b29d92e97240d', 'If you leave a comment on our site you may opt-in to saving your name, email address and website in cookies. These are for your convenience so that you do not have to fill in your details again when you leave another comment. These cookies will last for one year.', NULL, NULL, 'LINE', NULL, 2, '', 'd794b6153c4a4b8146e4b05cafb43a27', '', NULL),
(1654, 'en', 'default', 'cd7b8cf1cfc4fb579df5f63f419c3f5f', 'If you have an account and you log in to this site, we will set a temporary cookie to determine if your browser accepts cookies. This cookie contains no personal data and is discarded when you close your browser.', NULL, NULL, 'LINE', NULL, 2, '', '88498cc2eeef4905ce9452eb8241da52', '', NULL),
(1655, 'en', 'default', '85540ae266a03383e65007e160616c39', 'When you log in, we will also set up several cookies to save your login information and your screen display choices. Login cookies last for two days, and screen options cookies last for a year. If you select &quot;Remember Me&quot;, your login will persist for two weeks. If you log out of your account, the login cookies will be removed.', NULL, NULL, 'LINE', NULL, 2, '', '82da5429bc5561bbe23b1b043e08de1a', '', NULL),
(1656, 'en', 'default', '3f2b4cfd2cad80bf7584970a3f835c02', 'If you edit or publish an article, an additional cookie will be saved in your browser. This cookie includes no personal data and simply indicates the post ID of the article you just edited. It expires after 1 day.', NULL, NULL, 'LINE', NULL, 2, '', '1aefc2014eb00ab096a7cc8ca09b5ee2', '', NULL),
(1657, 'en', 'default', 'b7356884085f8ba4da0d0975927c6332', 'Embedded content from other websites', NULL, NULL, 'LINE', NULL, 2, '', '7107a4cd49e362905e46b41473054a9f', '', NULL),
(1658, 'en', 'default', '21619874853161da6da2aaf4f1352b7c', 'Articles on this site may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.', NULL, NULL, 'LINE', NULL, 2, '', '8a513931173c1a7136db5080a445e533', '', NULL),
(1659, 'en', 'default', '962c28891ea0971c27aec3bfc1781b57', 'These websites may collect data about you, use cookies, embed additional third-party tracking, and monitor your interaction with that embedded content, including tracking your interaction with the embedded content if you have an account and are logged in to that website.', NULL, NULL, 'LINE', NULL, 2, '', '07835bfb9b2600b722360fd372e71ff1', '', NULL),
(1660, 'en', 'default', 'a768caa988605a2846599cf7e2d0c26a', 'Analytics', NULL, NULL, 'LINE', NULL, 2, '', '70efa456583f37f77003d9f7871ac35d', '', NULL),
(1661, 'en', 'default', '48ac74d87e61fcb5c2adf64ca48cf64e', 'In this subsection you should note what analytics package you use, how users can opt out of analytics tracking, and a link to your analytics provider&#8217;s privacy policy, if any.', NULL, NULL, 'LINE', NULL, 2, '', 'ef6c1a3649fca4635c6d724a7e8e9440', '', NULL),
(1662, 'en', 'default', 'a9aaa5a8c454b3604df40c08faf864cb', 'By default WordPress does not collect any analytics data. However, many web hosting accounts collect some anonymous analytics data. You may also have installed a WordPress plugin that provides analytics services. In that case, add information from that plugin here.', NULL, NULL, 'LINE', NULL, 2, '', 'bbb10dc461ad05c0ba1743dbb84c6a33', '', NULL),
(1663, 'en', 'default', 'b36d0f4f434f0ca2b84a3507673dbf63', 'Who we share your data with', NULL, NULL, 'LINE', NULL, 2, '', 'cf98bfb7cb46423fcee1242d5f7f16d4', '', NULL),
(1664, 'en', 'default', '88afac7dd32e40bf990b8d3423dfcb5e', 'In this section you should name and list all third party providers with whom you share site data, including partners, cloud-based services, payment processors, and third party service providers, and note what data you share with them and why. Link to their own privacy policies if possible.', NULL, NULL, 'LINE', NULL, 2, '', 'bc39528f72dd657a817abfe0e70fa67f', '', NULL),
(1665, 'en', 'default', '72927f6ce8ef34a17482510ae8e43c73', 'By default WordPress does not share any personal data with anyone.', NULL, NULL, 'LINE', NULL, 2, '', '3e2c17f51ad2871c50487c676743a6bb', '', NULL),
(1666, 'en', 'default', '719a276cdff21fb86344c5e71684c4bc', 'How long we retain your data', NULL, NULL, 'LINE', NULL, 2, '', '84aa09d46aa51e6e1bf91dec8d05e034', '', NULL),
(1667, 'en', 'default', 'bb1232b0272e71fd61576bb759fe4fcb', 'In this section you should explain how long you retain personal data collected or processed by the web site. While it is your responsibility to come up with the schedule of how long you keep each dataset for and why you keep it, that information does need to be listed here. For example, you may want to say that you keep contact form entries for six months, analytics records for a year, and customer purchase records for ten years.', NULL, NULL, 'LINE', NULL, 2, '', 'ab44f2d90a4122efb4d73b1271ddcd1e', '', NULL),
(1668, 'en', 'default', 'b821f0a93d744e025849cd46e8b3baad', 'If you leave a comment, the comment and its metadata are retained indefinitely. This is so we can recognize and approve any follow-up comments automatically instead of holding them in a moderation queue.', NULL, NULL, 'LINE', NULL, 2, '', '2adafd60d076932c43e67845ed14dde0', '', NULL),
(1669, 'en', 'default', '522aadf23f305abae106a83bf430576e', 'For users that register on our website (if any), we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.', NULL, NULL, 'LINE', NULL, 2, '', 'e72f6eaabce2f1292d15c25394db0b83', '', NULL),
(1670, 'en', 'default', 'b77abd58d17fa7323ad0f0e230265377', 'What rights you have over your data', NULL, NULL, 'LINE', NULL, 2, '', 'eaaf98b0e5cc32f07bb473bb28a8162d', '', NULL),
(1671, 'en', 'default', '7b43f0a3a9bad71b5515c3febf1c4e85', 'In this section you should explain what rights your users have over their data and how they can invoke those rights.', NULL, NULL, 'LINE', NULL, 2, '', '1d494e615fc6d7af13588575aa5f764a', '', NULL),
(1672, 'en', 'default', 'ae1bc04b5e3967feb6adc498b8d57f62', 'If you have an account on this site, or have left comments, you can request to receive an exported file of the personal data we hold about you, including any data you have provided to us. You can also request that we erase any personal data we hold about you. This does not include any data we are obliged to keep for administrative, legal, or security purposes.', NULL, NULL, 'LINE', NULL, 2, '', 'c0b485544617c4aba2e6cc625c5732d8', '', NULL),
(1673, 'en', 'default', '79cc13f4de633a7fd0a8dc7c5c03de3d', 'Where we send your data', NULL, NULL, 'LINE', NULL, 2, '', 'dc9cfd4a3cd299346ceccf5ec2767598', '', NULL),
(1674, 'en', 'default', 'e033cd0f72a714684763bb3b10497ef1', 'In this section you should list all transfers of your site data outside the European Union and describe the means by which that data is safeguarded to European data protection standards. This could include your web hosting, cloud storage, or other third party services.', NULL, NULL, 'LINE', NULL, 2, '', '6bab02ff41df219f416fc29c55fd9271', '', NULL);
INSERT INTO `do_icl_strings` (`id`, `language`, `context`, `name`, `value`, `string_package_id`, `location`, `type`, `title`, `status`, `gettext_context`, `domain_name_context_md5`, `translation_priority`, `word_count`) VALUES
(1675, 'en', 'default', 'd3d0a99752e8793c0f2891b4a0dccd7d', 'European data protection law requires data about European residents which is transferred outside the European Union to be safeguarded to the same standards as if the data was in Europe. So in addition to listing where data goes, you should describe how you ensure that these standards are met either by yourself or by your third party providers, whether that is through an agreement such as Privacy Shield, model clauses in your contracts, or binding corporate rules.', NULL, NULL, 'LINE', NULL, 2, '', '6d750ee1a6f3c7ea9e7b07ab963c68f0', '', NULL),
(1676, 'en', 'default', '902789e4687e71730f1df3f80c6ddd6c', 'Visitor comments may be checked through an automated spam detection service.', NULL, NULL, 'LINE', NULL, 2, '', '64c67802c34cf8a8f08b4aea127700fb', '', NULL),
(1677, 'en', 'default', '06dbd0c1d81b8281a3bc9c21d59725c7', 'Your contact information', NULL, NULL, 'LINE', NULL, 2, '', '61f93c346edab7f02fa9c3dd24b19a36', '', NULL),
(1678, 'en', 'default', '562b66a0805d1853fa06902cdb6774d5', 'In this section you should provide a contact method for privacy-specific concerns. If you are required to have a Data Protection Officer, list their name and full contact details here as well.', NULL, NULL, 'LINE', NULL, 2, '', 'b783b38f8b0382d31f2e0d6b68dc26ff', '', NULL),
(1679, 'en', 'default', '0f68b904e33d9ac04605aecc958bcf52', 'Additional information', NULL, NULL, 'LINE', NULL, 2, '', 'deba883b423f91e94597e7842820df4b', '', NULL),
(1680, 'en', 'default', '28479dd04f0c4f904cbfc647df8a39f7', 'If you use your site for commercial purposes and you engage in more complex collection or processing of personal data, you should note the following information in your privacy policy in addition to the information we have already discussed.', NULL, NULL, 'LINE', NULL, 2, '', '26059df016ea17b5bee3abc3165cc69d', '', NULL),
(1681, 'en', 'default', 'eedb81c7ccdd675c82430eb4d06bcec2', 'How we protect your data', NULL, NULL, 'LINE', NULL, 2, '', 'e8cfa9664384947b51997f82809ceab5', '', NULL),
(1682, 'en', 'default', '9f2feaf2b829a5c3749cb3a0dec950a7', 'In this section you should explain what measures you have taken to protect your users&#8217; data. This could include technical measures such as encryption; security measures such as two factor authentication; and measures such as staff training in data protection. If you have carried out a Privacy Impact Assessment, you can mention it here too.', NULL, NULL, 'LINE', NULL, 2, '', '447f7c9e79da2eaf80b14485c81eef4a', '', NULL),
(1683, 'en', 'default', 'b913161fbf9e35c3c4cac8c6260eb2d6', 'What data breach procedures we have in place', NULL, NULL, 'LINE', NULL, 2, '', 'a15090ee82dea20072fad68f6639eaed', '', NULL),
(1684, 'en', 'default', 'cc868b88b34779f820016d60eeb4d13f', 'In this section you should explain what procedures you have in place to deal with data breaches, either potential or real, such as internal reporting systems, contact mechanisms, or bug bounties.', NULL, NULL, 'LINE', NULL, 2, '', '2e2f9f76d2e53db3e5050f6eed876131', '', NULL),
(1685, 'en', 'default', '41733fc9673b9e3189ef34fad3240ffd', 'What third parties we receive data from', NULL, NULL, 'LINE', NULL, 2, '', 'b549e8965e06eb74df89650d84634eeb', '', NULL),
(1686, 'en', 'default', 'b127c8939223b7c79e41917477d2e09e', 'If your web site receives data about users from third parties, including advertisers, this information must be included within the section of your privacy policy dealing with third party data.', NULL, NULL, 'LINE', NULL, 2, '', 'bdb7658161c5f57d631ee4aa4a631ecf', '', NULL),
(1687, 'en', 'default', 'a0b4dc8a5171d89c25ef2542dbcb8851', 'What automated decision making and/or profiling we do with user data', NULL, NULL, 'LINE', NULL, 2, '', '749c2bb3773d6ce13ee781eecd8943f4', '', NULL),
(1688, 'en', 'default', '3ac9c7fe01cf02c25932c2fce99d968a', 'If your web site provides a service which includes automated decision making - for example, allowing customers to apply for credit, or aggregating their data into an advertising profile - you must note that this is taking place, and include information about how that information is used, what decisions are made with that aggregated data, and what rights users have over decisions made without human intervention.', NULL, NULL, 'LINE', NULL, 2, '', 'c7ffd4403fc114954b738bbcbe9fea06', '', NULL),
(1689, 'en', 'default', '33dec286c87af12437699c4c1feeec9f', 'Industry regulatory disclosure requirements', NULL, NULL, 'LINE', NULL, 2, '', '701077419f0b3ee3eff24186dc2be58d', '', NULL),
(1690, 'en', 'default', '03191ad53bd55facfbe806e381b5aff0', 'If you are a member of a regulated industry, or if you are subject to additional privacy laws, you may be required to disclose that information here.', NULL, NULL, 'LINE', NULL, 2, '', 'd393238caa2225600eef5118eaa27ec1', '', NULL),
(1691, 'en', 'default', 'fde31686817fe5029025586d51b35861', 'WordPress', NULL, NULL, 'LINE', NULL, 2, '', '6757a2df47df1844cdca13c3a074863e', '', NULL),
(1692, 'en', 'sitepress', '02f3fa2daee80a38f0abef570480e1a9', 'WPML uses cookies to identify the visitor’s current language, the last visited language and the language of users who have logged in.', NULL, NULL, 'LINE', NULL, 0, '', 'a12ef1bfa210bafe575e5dc1a18442f1', '', NULL),
(1693, 'en', 'sitepress', '9227fba722c89495e19c0c23e8037df5', 'While you use the plugin, WPML will share data regarding the site through Installer. No data from the user itself will be shared.', NULL, NULL, 'LINE', NULL, 0, '', '731342eb174a568fe88e1ecc7f759ce4', '', NULL),
(1694, 'en', 'wpml-translation-management', '244eb82d865294130f57b9950a36b034', 'WPML Translation Management will send the email address and name of each manager and assigned translator as well as the content itself to Advanced Translation Editor and to the translation services which are used.', NULL, NULL, 'LINE', NULL, 10, '', '67a55feb2af7961bf06951ff1057822a', '', NULL),
(1695, 'en', 'wpml-string-translation', '66a6d6b2b2a6d1fb462747c264b9a641', 'WPML String Translation will send all strings to WPML’s Advanced Translation Editor and to the translation services which are used.', NULL, NULL, 'LINE', NULL, 10, '', '9a19296333e745f277699eb9dc7fd11e', '', NULL),
(1696, 'en', 'default', 'ae5c884bb6547b248e0ae42a44338dfb', '%1$s &lsaquo; %2$s &#8212; WordPress', NULL, NULL, 'LINE', NULL, 2, '', 'db168dd6972314ce3d64822dff014be7', '', NULL),
