(7853, 'en', 'default', 'fc276f47f9e88e53fb9a81e0e683a057', 'If you want to remove the widget but save its setting for possible future use, just drag it into the Inactive Widgets area. You can add them back anytime from there. This is especially helpful when you switch to a theme with fewer or different widget areas.', NULL, NULL, 'LINE', NULL, 2, '', 'f5f6f5978f1a37ee120927289f3f6f8d', '', NULL),
(7854, 'en', 'default', 'b54fa9afe2dc888d8a5c71cc1b61b51c', 'If you want to run multiple WordPress installations in a single database, change this.', NULL, NULL, 'LINE', NULL, 2, '', '7ed16a5d29814e6517b04e35979dbb8c', '', NULL),
(7855, 'en', 'default', 'ce99e8b4ddb1935ad216af170d465663', 'If you would like to see more plugins to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional plugins from the <a href=\"%s\">WordPress Plugin Directory</a>. Plugins in the WordPress Plugin Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!', NULL, NULL, 'LINE', NULL, 2, '', '918021f3c1c7333bbb985975acdf69b3', '', NULL),
(7856, 'en', 'default', 'ff1e9b8584871613da97bf909bf0d3d4', 'If you would like to see more themes to choose from, click on the &#8220;Add New&#8221; button and you will be able to browse or search for additional themes from the <a href=\"%s\">WordPress Theme Directory</a>. Themes in the WordPress Theme Directory are designed and developed by third parties, and are compatible with the license WordPress uses. Oh, and they&#8217;re free!', NULL, NULL, 'LINE', NULL, 2, '', '85b7f40dae14e2dedd2d051b8da068b4', '', NULL),
(7857, 'en', 'default', '5074f6b262894aae8f6923d7ff9eadf1', 'If your %1$s file was <a href=\"%2$s\">writable</a>, we could do this automatically, but it isn&#8217;t so these are the mod_rewrite rules you should have in your %1$s file. Click in the field and press %3$s to select all.', NULL, NULL, 'LINE', NULL, 2, '', '0651b69befec7bf871bf4d593b4eb1cf', '', NULL),
(7858, 'en', 'default', '93f3cd2b6a8fda8223f25e6fd0f47db4', 'If your %1$s file was <a href=\"%2$s\">writable</a>, we could do this automatically, but it isn&#8217;t so this is the url rewrite rule you should have in your %1$s file. Click in the field and press %3$s to select all. Then insert this rule inside of the %4$s element in %1$s file.', NULL, NULL, 'LINE', NULL, 2, '', '4768687268baff9591b17ec71c06e3d6', '', NULL),
