(9142, 'en', 'default', '19b8a23b0794f1d1b2353f149aa2a575', 'WordPress Events and News', NULL, NULL, 'LINE', NULL, 2, '', '4449065eccd9c6d7a467f72174a20ed6', '', NULL),
(9143, 'en', 'default', '7ec7508c587f87f5a01c3a078d6a6dab', 'WordPress Updates', NULL, NULL, 'LINE', NULL, 2, '', '1aeff3f3a99a487616cb48bba3497496', '', NULL),
(9144, 'en', 'default', '33c13135df9264a73a6958ca2ae370f5', 'WordPress can also attempt to optimize the database. This improves performance in some situations. Repairing and optimizing the database can take a long time and the database will be locked while optimizing.', NULL, NULL, 'LINE', NULL, 2, '', '83b16757e71c057b4ab547e2625df230', '', NULL),
(9145, 'en', 'default', '68ff8e47448b84d116faca89d63a3125', 'WordPress can automatically look for some common database problems and repair them. Repairing can take a while, so please be patient.', NULL, NULL, 'LINE', NULL, 2, '', '081462fd6d52c8ec3561736fd9b1b7d5', '', NULL),
(9146, 'en', 'default', 'd1e356a8b5c46bde84443e397a38b9d0', 'WordPress database repair', NULL, NULL, 'LINE', NULL, 2, '', '897cf3b84835381a1682a312c55c2546', '', NULL),
(9147, 'en', 'default', '920b86d822cd25a2a2c592134fd08ce1', 'WordPress grows when people like you tell their friends about it, and the thousands of businesses and services that are built on and around WordPress share that fact with their users. We&#8217;re flattered every time someone spreads the good word, just make sure to <a href=\"%s\">check out our trademark guidelines</a> first.', NULL, NULL, 'LINE', NULL, 2, '', '84d9b5767192db384ce1c4e4cddb9c27', '', NULL),
(9148, 'en', 'default', 'ca2e1c6fdebc7887415465aaaf360960', 'WordPress has been installed. Thank you, and enjoy!', NULL, NULL, 'LINE', NULL, 2, '', '1745013426dd7b654f51d4fb2a69e0b2', '', NULL),
(9149, 'en', 'default', '924b4ada54bcaa491660c0f5f200b5dd', 'WordPress has been updated! Before we send you on your way, we have to update your database to the newest version.', NULL, NULL, 'LINE', NULL, 2, '', '3c03c037f9d0fb84ed283d222a10bfeb', '', NULL),
(9150, 'en', 'default', '87488821d95fad046e6934ba9a27f016', 'WordPress is Free and open source software, built by a distributed community of mostly volunteer developers from around the world. WordPress comes with some awesome, worldview-changing rights courtesy of its <a href=\"%s\">license</a>, the GPL.', NULL, NULL, 'LINE', NULL, 2, '', '32bda0a1395f3a803bf05a0164f36dee', '', NULL),
(9151, 'en', 'default', 'ba5a6fbddf1b8a34c5384891c777374f', 'WordPress is at the latest version.', NULL, NULL, 'LINE', NULL, 2, '', 'c4ecb640802ec497b6f8133c3ef076ff', '', NULL),
(9152, 'en', 'default', '98139c5279fc772772d86ebf86967fbf', 'WordPress is created by a <a href=\"%1$s\">worldwide team</a> of passionate individuals. <a href=\"%2$s\">Get involved in WordPress</a>.', NULL, NULL, 'LINE', NULL, 2, '', '3731b77e9b5aaccad48ba29b43de7270', '', NULL),
(9153, 'en', 'default', '82f771478b6cafba7b96c61a3299c075', 'WordPress is created by a worldwide team of passionate individuals.', NULL, NULL, 'LINE', NULL, 2, '', '01c7f502da6911611ca2c5ec1747b156', '', NULL),
(9154, 'en', 'default', '4b8a79aa611a3d5388694ff8ce54bdfb', 'WordPress is not notifying any <a href=\"%1$s\">Update Services</a> because of your site&#8217;s <a href=\"%2$s\">visibility settings</a>.', NULL, NULL, 'LINE', NULL, 2, '', 'ad7215a97613adc28341716934624415', '', NULL),
(9155, 'en', 'default', '61a8161aaa6fd8fe6e6203323a3587a3', 'WordPress is working on a new way to create and control your content and we&#8217;d love to have your help. Interested in being an <a href=\"%s\">early tester</a> or getting involved with the Gutenberg project? <a href=\"%s\">Contribute on GitHub</a>.', NULL, NULL, 'LINE', NULL, 2, '', '5b7f9a3cb1b713e4f757f0eb4dba8f87', '', NULL),
(9156, 'en', 'default', '002ed833af5d0c8a84bbc41ef94724dc', 'WordPress offers you the ability to create a custom URL structure for your permalinks and archives. Custom URL structures can improve the aesthetics, usability, and forward-compatibility of your links. A <a href=\"%s\">number of tags are available</a>, and here are some examples to get you started.', NULL, NULL, 'LINE', NULL, 2, '', '4ca5dedd93f643351f1ca0a14baf5b97', '', NULL),
(9157, 'en', 'default', '145e951a918d629a05851929a786f394', 'WordPress should correct invalidly nested XHTML automatically', NULL, NULL, 'LINE', NULL, 2, '', '2bd8338d56622879b225e000f36be25b', '', NULL),
(9158, 'en', 'default', '5110980b4815813643ebd02608039bd4', 'WordPress site: %s', NULL, NULL, 'LINE', NULL, 2, '', '23f23694cfb8af8ec16004989d3d5c2a', '', NULL),
(9159, 'en', 'default', '603f15dee5254744413440d0193f4ca7', 'WordPress updated successfully', NULL, NULL, 'LINE', NULL, 2, '', '6cb4ca7c479b73bdfc4284a43d4ca056', '', NULL),
