(9163, 'en', 'default', 'ba50be3c6c46d860eb3f8c7856a846bf', 'X &#8212; Even more mature than above', NULL, NULL, 'LINE', NULL, 2, '', '8ccad14cfa25a65729d673e5b6351aac', '', NULL),
(9164, 'en', 'default', '8bc3b8275af78e8ebd86a4abb43913c8', 'XFN stands for <a href=\"http://gmpg.org/xfn/\">XHTML Friends Network</a>, which is optional. WordPress allows the generation of XFN attributes to show how you are related to the authors/owners of the site to which you are linking.', NULL, NULL, 'LINE', NULL, 2, '', '92d4d64451b5fdcc0f814b28ddb66541', '', NULL),
