(9168, 'en', 'default', 'ee6b36df0a0e5ae29804573f7bc0f5ec', 'Yes, take me to my profile page', NULL, NULL, 'LINE', NULL, 2, '', '1d3042fbf321b0f29a212ba1fa41b065', '', NULL),
(9169, 'en', 'default', '59f603400b805b1de2d465999a14a8d1', 'Yes, you read that right. Just like you can draft and revise posts and schedule them to go live on the date and time you choose, you can now tinker with your site&#8217;s design and schedule those design changes to go live as you please.', NULL, NULL, 'LINE', NULL, 2, '', '92a26f1aeae59b581679ae5b9662ea6d', '', NULL),
(9170, 'en', 'default', '7cff9b6df2ec3f0734a9bfa274ad5b71', 'You also have some plugins or themes with updates available. Update them now:', NULL, NULL, 'LINE', NULL, 2, '', '2b582a384405ef134b2e3732c6752c15', '', NULL),
(9171, 'en', 'default', 'f8ba38900d10b5b21aee002e9e7bd51b', 'You appear to be making direct edits to your plugin in the WordPress dashboard. We recommend that you don&#8217;t! Editing plugins directly may introduce incompatibilities that break your site and your changes may be lost in future updates.', NULL, NULL, 'LINE', NULL, 2, '', 'f826278325752cd0128cabb05d582280', '', NULL),
(9172, 'en', 'default', '8cc087cec19b11374357e1838a97fdd2', 'You appear to be making direct edits to your theme in the WordPress dashboard. We recommend that you don&#8217;t! Editing your theme directly could break your site and your changes may be lost in future updates. If you need to tweak more than your theme&#8217;s CSS, you might want to try <a href=\"%s\">making a child theme</a>.', NULL, NULL, 'LINE', NULL, 2, '', 'b9f6841fdabf7c589997152eee94c053', '', NULL),
