(9177, 'en', 'default', 'ac4f38ab41fe32062fad243041ab44ad', 'You are about to delete this theme \'%s\'\\n  \'Cancel\' to stop, \'OK\' to delete.', NULL, NULL, 'LINE', NULL, 2, '', 'b49de564fe6352d019b2c60f0bf6aaa3', '', NULL),
(9178, 'en', 'default', '0232103929f5d403f6c7958a3b807a8d', 'You are about to install WordPress %s <strong>in English (US).</strong> There is a chance this update will break your translation. You may prefer to wait for the localized version to be released.', NULL, NULL, 'LINE', NULL, 2, '', '157295ad1a6686eb670ef26a04b03842', '', NULL),
(9179, 'en', 'default', '3b4ff5a4b188de3878909bd3a9b3cec8', 'You are about to mark the following comment as spam:', NULL, NULL, 'LINE', NULL, 2, '', 'c16df9f52a96623f2edb6aac33a659b1', '', NULL),
